import './App.css';
import React, { Component } from 'react'
import { CardsHolder } from './components/cards-holder/cards-holder.component'

class App extends Component {

  //ctor
  constructor() {
    super();
    // the state  properties
    this.state = {
      myMonsters: []
    }
  }

  // methods
  //* gets the data from the API and puts it inside the state property
  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(res => res.json())
      .then(resNames => this.setState(
        { myMonsters: resNames }
      ));
  }

  render() {

    return (
      <div className="App">
        <CardsHolder monsters = {this.stamyMonsters}/*name="First"*/>


        </CardsHolder>
      </div>
    );
  }
};

export default App;
